function add(n1: number, n2: number) {
    return n1 + n2;
}

function printResult(num: number) {
    console.log('Result' + num);
}
function addCallBack(n1: number, n2: number, cb: (num: number) => void) {
    const result = n1 + n2;
    cb(result);
}
printResult(add(12, 14));

let combinedVale: (a: number, b: number) => number;
combinedVale = add;


console.log(combinedVale(12, 12));

addCallBack(10, 20, (result) => {
    console.log(result);
})